mapper.select:
SELECT ID, TITLE, TIME, NUMBER, BIGHEADTITLE
FROM NOTICE_BOARD
WHERE BIGHEADTITLE=#{BIGHEADTITLE}
ORDER BY NUMBER DESC limit 3;

SELECT ID, TITLE, HEADTITLE, NAME, NUMBER, TIME, BIGHEADTITLE
FROM NOTICE_BOARD
WHERE BIGHEADTITLE=#{BIGHEADTITLE}
ORDER BY NUMBER DESC LIMIT #{start}, #{end};

SELECT COUNT(*)
FROM NOTICE_BOARD
WHERE BIGHEADTITLE = #{BIGHEADTITLE};

SELECT ID, TITLE, HEADTITLE, NAME, NUMBER, TIME, BIGHEADTITLE
FROM NOTICE_BOARD
WHERE ${SEARCHTITLE} = #{SEARCH}
    AND BIGHEADTITLE = #{BIGHEADTITLE}
    LIMIT #{start}, #{end};

mapper.update:
UPDATE NOTICE_BOARD
SET TITLE     = #{TITLE},
    HEADTITLE = #{HEADTITLE},
    CONTENTS  = #{CONTENTS},
    TIME      = #{TIME}
WHERE ID = #{ID}
  AND NAME = #{NAME}
  AND BIGHEADTITLE = #{BIGHEADTITLE}
  AND NUMBER = #{NUMBER};
