mapper.select:
select ID, TITLE, TIME, NUMBER, BIGHEADTITLE
from NOTICE_BOARD
where BIGHEADTITLE=#{BIGHEADTITLE}
order by TIME desc limit 3;

select ID, TITLE, HEADTITLE, NAME, NUMBER, TIME, BIGHEADTITLE
FROM NOTICE_BOARD
WHERE BIGHEADTITLE=#{BIGHEADTITLE};

SELECT ID, TITLE, HEADTITLE, NAME, NUMBER, TIME, BIGHEADTITLE
FROM NOTICE_BOARD
WHERE ${TITLE} = #{SEARCH}
  AND BIGHEADTITLE = #{BIGHEADTITLE};

mapper.update:
update NOTICE_BOARD
set TITLE     = #{TITLE},
    HEADTITLE = #{HEADTITLE},
    CONTENTS  = #{CONTENTS},
    TIME      = #{TIME}
where ID = #{ID}
  AND NAME = #{NAME}
  AND BIGHEADTITLE = #{BIGHEADTITLE}
  AND NUMBER = #{NUMBER};

mapper.select:
select ID, TITLE, HEADTITLE, NAME, NUMBER, TIME, BIGHEADTITLE
from NOTICE_BOARD
WHERE BIGHEADTITLE=#{BIGHEADTITLE} LIMIT #{startPage}, #{PerPage};

SELECT COUNT(*)
FROM NOTICE_BOARD
WHERE BIGHEADTITLE = #{BIGHEADTITLE};
